// UI
function royal_customizer_enqueue_scripts() {

	// send home url to javascript
	$royal_home_url = array( esc_url(get_stylesheet_directory_uri()) );
	
	// Register
	wp_register_script( 'royal-customizer-ui', THEMEROOT .'/framework/customizer/js/customizer-ui.min.js', array('jquery'), null, true );

	wp_register_style( 'royal-customizer-ui', THEMEROOT .'/framework/customizer/css/customizer-ui.css' );
	wp_register_style( 'royal-fontawesome', THEMEROOT .'/css/font-awesome.min.css' );
	
	// Enqueue
	wp_enqueue_script( 'royal-customizer-ui' );

	// jquery ui scripts
	wp_enqueue_script( 'jquery-ui-tabs' );
	wp_enqueue_script( 'jquery-ui-button' );
	wp_enqueue_script( 'jquery-ui-slider' );
	wp_enqueue_script( 'wp-color-picker' );

	// styles
	wp_enqueue_style( 'royal-customizer-ui' );
	wp_enqueue_style( 'royal-fontawesome' );
	wp_enqueue_style( 'wp-color-picker' );

}

add_action( 'customize_controls_enqueue_scripts', 'royal_customizer_enqueue_scripts' );


